import React, { useState, useEffect } from 'react';
import axios from 'axios';

// Set the base URL for axios requests
axios.defaults.baseURL = 'http://localhost:5000';

const Tenders = () => {
  const [state, setState] = useState('');
  const [district, setDistrict] = useState('');
  const [department, setDepartment] = useState('');
  const [showExpired, setShowExpired] = useState(false);
  const [tenders, setTenders] = useState([]);
  const [dropdownOptions, setDropdownOptions] = useState({
    states: [],
    districts: [],
    departments: [],
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [tendersPerPage, setTendersPerPage] = useState(10);
  const [currentPage, setCurrentPage] = useState(1);

  useEffect(() => {
    fetchDropdownOptions();
  }, []);

  const fetchDropdownOptions = async () => {
    try {
      const response = await axios.get('/api/dropdown-options');
      setDropdownOptions(response.data);
    } catch (err) {
      setError('Error fetching dropdown options');
    }
  };

  const fetchTendersData = async () => {
    setLoading(true);
    setError('');
    try {
      const response = await axios.post('/api/tenders', {
        state,
        district,
        department,
        showExpired,
      });
      setTenders(response.data.tenders);
      setCurrentPage(1);
    } catch (err) {
      setError('Error fetching tenders data');
    } finally {
      setLoading(false);
    }
  };

  const handleStateChange = (e) => {
    const selectedState = e.target.value;
    setState(selectedState);
    setDistrict('');
    setDepartment('');
    if (selectedState) {
      axios.get(`/api/districts/${selectedState}`)
        .then(response => setDropdownOptions(prev => ({ ...prev, districts: response.data })))
        .catch(() => setError('Error fetching districts'));
    } else {
      setDropdownOptions(prev => ({ ...prev, districts: [] }));
    }
  };

  const handleDistrictChange = (e) => {
    const selectedDistrict = e.target.value;
    setDistrict(selectedDistrict);
    setDepartment('');
    if (selectedDistrict) {
      axios.get(`/api/departments/${state}/${selectedDistrict}`)
        .then(response => setDropdownOptions(prev => ({ ...prev, departments: response.data })))
        .catch(() => setError('Error fetching departments'));
    } else {
      setDropdownOptions(prev => ({ ...prev, departments: [] }));
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    fetchTendersData();
  };

  const paginatedTenders = tenders.slice(
    (currentPage - 1) * tendersPerPage,
    currentPage * tendersPerPage
  );

  const totalPages = Math.ceil(tenders.length / tendersPerPage);

  return (
    <div className="container mx-auto p-4">
      <form onSubmit={handleSubmit} className="mb-8">
        <div className="mb-4">
          <label className="block mb-2">State:</label>
          <select
            className="w-full p-2 border rounded"
            value={state}
            onChange={handleStateChange}
          >
            <option value="">Select a state</option>
            {dropdownOptions.states.map((s) => (
              <option key={s} value={s}>{s}</option>
            ))}
          </select>
        </div>
        <div className="mb-4">
          <label className="block mb-2">District:</label>
          <select
            className="w-full p-2 border rounded"
            value={district}
            onChange={handleDistrictChange}
            disabled={!state}
          >
            <option value="">Select a district</option>
            {dropdownOptions.districts.map((d) => (
              <option key={d} value={d}>{d}</option>
            ))}
          </select>
        </div>
        <div className="mb-4">
          <label className="block mb-2">Department:</label>
          <select
            className="w-full p-2 border rounded"
            value={department}
            onChange={(e) => setDepartment(e.target.value)}
            disabled={!district}
          >
            <option value="">Select a department</option>
            {dropdownOptions.departments.map((d) => (
              <option key={d} value={d}>{d}</option>
            ))}
          </select>
        </div>
        <div className="mb-4">
          <label className="flex items-center">
            <input
              type="checkbox"
              className="mr-2"
              checked={showExpired}
              onChange={(e) => setShowExpired(e.target.checked)}
            />
            Show Expired Tenders
          </label>
        </div>
        <div className="mb-4">
          <label className="block mb-2">Tenders per page:</label>
          <select
            className="w-full p-2 border rounded"
            value={tendersPerPage}
            onChange={(e) => setTendersPerPage(Number(e.target.value))}
          >
            <option value={5}>5</option>
            <option value={10}>10</option>
            <option value={20}>20</option>
            <option value={50}>50</option>
          </select>
        </div>
        <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded">
          Submit
        </button>
      </form>

      {loading && <p>Loading...</p>}
      {error && <p className="text-red-500">{error}</p>}

      <div>
        {paginatedTenders.length > 0 ? (
          <>
            <ul className="space-y-4">
              {paginatedTenders.map((tender) => (
                <li key={tender.tender_id} className="border p-4 rounded">
                  <h3 className="text-lg font-bold">{tender.title}</h3>
                  <p>State: {tender.state}</p>
                  <p>District: {tender.district}</p>
                  <p>Department: {tender.org_name}</p>
                  <p>Deadline: {tender.deadline}</p>
                </li>
              ))}
            </ul>
            <div className="mt-4 flex justify-between items-center">
              <button
                onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
                disabled={currentPage === 1}
                className="bg-gray-300 px-4 py-2 rounded disabled:opacity-50"
              >
                Previous
              </button>
              <span>Page {currentPage} of {totalPages}</span>
              <button
                onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))}
                disabled={currentPage === totalPages}
                className="bg-gray-300 px-4 py-2 rounded disabled:opacity-50"
              >
                Next
              </button>
            </div>
          </>
        ) : (
          <p>No tenders found.</p>
        )}
      </div>
    </div>
  );
};

export default Tenders;
