import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Tenders = () => {
  const [state, setState] = useState('');
  const [district, setDistrict] = useState('');
  const [department, setDepartment] = useState('');
  const [showExpired, setShowExpired] = useState(false);
  const [tenders, setTenders] = useState([]);
  const [dropdownOptions, setDropdownOptions] = useState({
    states: [],
    districts: [],
    departments: []
  });

  useEffect(() => {
    fetchTendersData();
  }, []);

  const fetchTendersData = async () => {
    try {
      const response = await axios.post('/api/tenders', {
        state,
        district,
        department,
        showExpired
      });
      setTenders(response.data.tenders);
      setDropdownOptions(response.data.dropdownOptions);
    } catch (error) {
      console.error('Error fetching tenders data', error);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    fetchTendersData();
  };

  const handleStateChange = (e) => {
    const newState = e.target.value;
    setState(newState);
    setDistrict('');  // Reset district when state changes
    fetchTendersData();  // Fetch new data when state changes
  };

  return (
    <div className="container mx-auto p-4">
      <form onSubmit={handleSubmit} className="mb-8">
        <div className="mb-4">
          <label className="block mb-2">State:</label>
          <select
            className="w-full p-2 border rounded"
            value={state}
            onChange={handleStateChange}
          >
            <option value="">Select a state</option>
            {dropdownOptions.states.map((s) => (
              <option key={s} value={s}>{s}</option>
            ))}
          </select>
        </div>
        <div className="mb-4">
          <label className="block mb-2">District:</label>
          <select
            className="w-full p-2 border rounded"
            value={district}
            onChange={(e) => setDistrict(e.target.value)}
            disabled={!state}
          >
            <option value="">Select a district</option>
            {dropdownOptions.districts.map((d) => (
              <option key={d} value={d}>{d}</option>
            ))}
          </select>
        </div>
        <div className="mb-4">
          <label className="block mb-2">Department:</label>
          <select
            className="w-full p-2 border rounded"
            value={department}
            onChange={(e) => setDepartment(e.target.value)}
          >
            <option value="">Select a department</option>
            {dropdownOptions.departments.map((d) => (
              <option key={d} value={d}>{d}</option>
            ))}
          </select>
        </div>
        <div className="mb-4">
          <label className="flex items-center">
            <input
              type="checkbox"
              className="mr-2"
              checked={showExpired}
              onChange={(e) => setShowExpired(e.target.checked)}
            />
            Show Expired Tenders
          </label>
        </div>
        <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded">Search</button>
      </form>

      <div>
        {tenders.length > 0 ? (
          <ul className="space-y-4">
            {tenders.map((tender) => (
              <li key={tender.tender_id} className="border p-4 rounded">
                <h3 className="text-xl font-bold">{tender.title}</h3>
                <p className="text-gray-600">{tender.org_name}</p>
                <p className="text-gray-600">{tender.district}, {tender.state}</p>
                <p className={tender.expired ? 'text-red-500' : 'text-green-500'}>
                  {tender.expired ? 'Expired' : 'Active'}
                </p>
              </li>
            ))}
          </ul>
        ) : (
          <p className="text-center text-gray-600">No tenders found</p>
        )}
      </div>
    </div>
  );
};

export default Tenders;
