import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import queryString from 'query-string';
import Papa from 'papaparse';

const TenderList = () => {
  const [tenders, setTenders] = useState([]);
  const [totalPages, setTotalPages] = useState(1);
  const [page, setPage] = useState(1);
  const [limit] = useState(10); // You can change the limit as needed

  const location = useLocation();
  const { state, district, department } = queryString.parse(location.search);

  useEffect(() => {
    const fetchTenders = async () => {
      try {
        const response = await fetch(
          `/tenders/list?state=${state}&district=${district}&department=${department}&page=${page}&limit=${limit}`
        );
        const data = await response.json();
        setTenders(data.tenders);
        setTotalPages(data.totalPages);
      } catch (error) {
        console.error('Error fetching tenders:', error);
      }
    };

    fetchTenders();
  }, [state, district, department, page, limit]);

  useEffect(() => {
    // Fetch the CSV file
    fetch('/path/to/output.csv')
      .then(response => response.text())
      .then(csvText => {
        // Parse the CSV text
        Papa.parse(csvText, {
          header: true,
          complete: (results) => {
            setTenders(results.data);
          },
        });
      });
  }, []);

  return (
    <div className="mt-5 pt-4">
      <h1 className="my-4 text-center">
        Latest {department} Tenders in {district}, {state}
      </h1>
      <table className="table table-striped">
        <thead>
          <tr>
            <th>Tender ID</th>
            <th>Title</th>
            <th>Organization Name</th>
            <th>District</th>
            <th>State</th>
            <th>Expired</th>
          </tr>
        </thead>
        <tbody>
          {tenders.map((tender, index) => (
            <tr key={index}>
              <td>{tender.tender_id}</td>
              <td>{tender.title}</td>
              <td>{tender.org_name}</td>
              <td>{tender.district}</td>
              <td>{tender.state}</td>
              <td>{tender.expired}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <div className="pagination">
        {Array.from({ length: totalPages }, (_, i) => (
          <button key={i} onClick={() => setPage(i + 1)} disabled={page === i + 1}>
            {i + 1}
          </button>
        ))}
      </div>
    </div>
  );
};

export default TenderList;
