import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import Cookies from 'js-cookie';
import './Login.css';

const Login = ({ setAuth }) => {
  const [formData, setFormData] = useState({ mobileNo: '', password: '' });
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError('');
    try {
      console.log('Attempting login with:', { mobileNo: formData.mobileNo, passwordLength: formData.password.length });
      const response = await axios.post('http://localhost:5000/api/login', formData);
      console.log('Login response:', response.data);
      if (response.data.success) {
        Cookies.set('auth', 'true');
        setAuth(true);
        navigate('/');
      } else {
        setError(response.data.message || 'An unexpected error occurred during login');
      }
    } catch (err) {
      console.error('Login error:', err);
      if (err.response) {
        console.log('Error response:', err.response.data);
        setError(err.response.data.message || 'An error occurred during login');
      } else if (err.request) {
        console.log('Error request:', err.request);
        setError('No response received from the server. Please try again.');
      } else {
        console.log('Error message:', err.message);
        setError('An unexpected error occurred. Please try again.');
      }
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      {error && <div className="alert error">{error}</div>}
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="mobileNo">Mobile Number:</label>
          <input
            type="text"
            id="mobileNo"
            name="mobileNo"
            className="form-control"
            value={formData.mobileNo}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            name="password"
            className="form-control"
            value={formData.password}
            onChange={handleChange}
            required
            minLength="8"
          />
        </div>
        <button type="submit" className="btn btn-primary w-100" disabled={isLoading}>
          {isLoading ? 'Logging in...' : 'Login'}
        </button>
        <a href="/password-reset/request-otp">Forgot Password?</a>
      </form>
      <div className="text-center mt-3">
        <p>Don't have an account? <a href="/register">Register here</a></p>
      </div>
    </div>
  );
};

export default Login;
