import React, { useState, useCallback } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import Cookies from 'js-cookie';
import './Login.css';

const Login = ({ setAuth }) => {
  const [formData, setFormData] = useState({ mobileNo: '', password: '' });
  const [submitStatus, setSubmitStatus] = useState({ error: '', isLoading: false });
  const navigate = useNavigate();

  const handleChange = useCallback((e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitStatus({ error: '', isLoading: true });

    try {
      const response = await axios.post('http://localhost:5000/api/login', formData);
      
      if (response.data.success) {
        Cookies.set('auth', 'true');
        setAuth(true);
        navigate('/');
      } else {
        setSubmitStatus({ error: response.data.message || 'An unexpected error occurred during login', isLoading: false });
      }
    } catch (err) {
      console.error('Login error:', err);
      const errorMessage = err.response?.data?.message || 'An unexpected error occurred. Please try again.';
      setSubmitStatus({ error: errorMessage, isLoading: false });
    }
  };

  const renderFormField = (name, label, type = "text") => (
    <div className="form-group">
      <label htmlFor={name}>{label}</label>
      <input
        type={type}
        id={name}
        name={name}
        className="form-control"
        value={formData[name]}
        onChange={handleChange}
        required
        minLength={type === "password" ? "8" : undefined}
      />
    </div>
  );

  return (
    <div className="login-container">
      <h2>Login</h2>
      {submitStatus.error && <div className="alert error">{submitStatus.error}</div>}
      <form onSubmit={handleSubmit}>
        {renderFormField("mobileNo", "Mobile Number")}
        {renderFormField("password", "Password", "password")}
        <button type="submit" className="btn btn-primary w-100" disabled={submitStatus.isLoading}>
          {submitStatus.isLoading ? 'Logging in...' : 'Login'}
        </button>
      </form>
      <div className="additional-links">
        <Link to="/password-reset/request-otp">Forgot Password?</Link>
        <p>Don't have an account? <Link to="/register">Register here</Link></p>
      </div>
    </div>
  );
};

export default Login;
