import React, { useState, useEffect, lazy, Suspense } from "react";
import { BrowserRouter as Router, Route, Routes, Navigate } from "react-router-dom";
import Cookies from 'js-cookie';
import Layout from "../components/Layout/Layout";
import ErrorBoundary from "../components/ErrorBoundary/ErrorBoundary";
import PrivateRoute from "./PrivateRoute";
import "../styles.css";
import AnimatedLoading from "./AnimatedLoading";

// Server-side imports
import express from 'express';
import cors from 'cors';
import { MongoClient } from 'mongodb';

// Lazy-loaded components
const HomePage = lazy(() => import("../components/Home/Home"));
const Login = lazy(() => import("../components/Login/Login"));
const Register = lazy(() => import("../components/Login/Register"));
const PasswordReset = lazy(() => import("../components/Login/PasswordReset"));
const RequestOtp = lazy(() => import("../components/Login/RequestOtp"));
const VerifyOtp = lazy(() => import("../components/Login/VerifyOtp"));
const Subscriptions = lazy(() => import("../components/Subscriptions"));
const Mission = lazy(() => import("../components/Mission/Mission"));
const Services = lazy(() => import("../components/Services/ServicesSection"));
const CoreValues = lazy(() => import("../components/CoreValues/CoreValues"));
const Reviews = lazy(() => import("../components/Reviews"));
const PrivacyPolicy = lazy(() => import("../components/Policy/PrivacyPolicy"));
const UploadDocuments = lazy(() => import("../components/UploadDocuments"));
const ContactUs = lazy(() => import("../components/ContactUs/ContactUs"));
const ProtectedComponent = lazy(() => import("./ProtectedComponent"));
const Tenders = lazy(() => import("../components/Tender/Tenders"));

// Server configuration
const app = express();
const port = 5000;

const mongoUrl = 'mongodb+srv://m84719666:d6Rjb4DyVuasNDrn@tendertesting.zygfo.mongodb.net/?retryWrites=true&w=majority&appName=tenderTesting';
const dbNameRegistration = 'Registered';
const dbNameTenders = 'Output';

app.use(cors());
app.use(express.json());

let client;

async function connectToMongo() {
  try {
    client = new MongoClient(mongoUrl, { useUnifiedTopology: true });
    await client.connect();
    console.log('Connected to MongoDB');
  } catch (error) {
    console.error('Failed to connect to MongoDB', error);
    process.exit(1);
  }
}

// Server-side endpoints
app.post('/api/register', async (req, res) => {
  try {
    const { username, mobileNo, email, password, preferences } = req.body;
    const db = client.db(dbNameRegistration);
    const usersCollection = db.collection('users');

    const existingUser = await usersCollection.findOne({ $or: [{ mobileNo }, { email }] });
    if (existingUser) {
      return res.status(400).json({ message: 'User already exists' });
    }

    const result = await usersCollection.insertOne({
      username,
      mobileNo,
      email,
      password, 
      preferences
    });

    res.status(201).json({ message: 'User registered successfully', id: result.insertedId });
  } catch (error) {
    console.error('Registration error:', error);
    res.status(500).json({ message: 'Error registering user' });
  }
});

app.post('/api/login', async (req, res) => {
  try {
    const { mobileNo, password } = req.body;
    console.log('Login attempt:', { mobileNo, passwordLength: password ? password.length : 0 });
    
    const db = client.db(dbNameRegistration);
    const user = await db.collection('users').findOne({ mobileNo });

    if (!user) {
      return res.status(401).json({ message: 'User not found' });
    }

    if (user.password !== password) {
      return res.status(401).json({ message: 'Incorrect password' });
    }

    console.log('Login successful:', mobileNo);
    res.json({ 
      success: true, 
      message: 'Login successful', 
      user: { mobileNo: user.mobileNo, preferences: user.preferences } 
    });
  } catch (error) {
    console.error('Login error:', error);
    res.status(500).json({ message: 'Error logging in', error: error.message });
  }
});

app.get('/api/states', async (req, res) => {
  try {
    const db = client.db(dbNameTenders);
    const tendersCollection = db.collection('Tenders');
    const states = await tendersCollection.distinct('state');
    res.json({ states });
  } catch (error) {
    console.error('Error fetching states:', error);
    res.status(500).json({ message: 'Error fetching states' });
  }
});

app.get('/api/districts/:state', async (req, res) => {
  try {
    const { state } = req.params;
    const db = client.db(dbNameTenders);
    const tendersCollection = db.collection('Tenders');
    const districts = await tendersCollection.distinct('district', { state });
    res.json({ districts });
  } catch (error) {
    console.error('Error fetching districts:', error);
    res.status(500).json({ message: 'Error fetching districts' });
  }
});

app.get('/api/departments/:state', async (req, res) => {
  try {
    const { state } = req.params;
    const db = client.db(dbNameTenders);
    const tendersCollection = db.collection('Tenders');
    const departments = await tendersCollection.distinct('org_name', { state });
    res.json({ departments });
  } catch (error) {
    console.error('Error fetching departments:', error);
    res.status(500).json({ message: 'Error fetching departments' });
  }
});

app.get('/api/tenders', async (req, res) => {
  try {
    const { state, district, department, showExpired } = req.query;
    const db = client.db(dbNameTenders);
    const tendersCollection = db.collection('Tenders');

    let query = {};
    if (state) query.state = state;
    if (district) query.district = district;
    if (department) query.org_name = department;
    if (showExpired === 'false') query.expired = false;

    const tenders = await tendersCollection.find(query).toArray();
    res.json({ tenders });
  } catch (error) {
    console.error('Error fetching tenders:', error);
    res.status(500).json({ message: 'Error fetching tenders' });
  }
});

// React component
function App() {
  const [auth, setAuth] = useState(Cookies.get('auth') === 'true');

  useEffect(() => {
    Cookies.set('auth', auth);
  }, [auth]);

  const handleLogout = () => {
    setAuth(false);
    Cookies.remove('auth');
  };

  return (
    <Router>
      <ErrorBoundary>
        <Layout auth={auth} handleLogout={handleLogout}>
          <Suspense fallback={<AnimatedLoading />}>
            <Routes>
              <Route path="/" element={<HomePage />} />
              <Route path="/login" element={<Login setAuth={setAuth} />} />
              <Route path="/register" element={<Register />} />
              <Route path="/password-reset" element={<PasswordReset />} />
              <Route path="/password-reset/request-otp" element={<RequestOtp />} />
              <Route path="/password-reset/verify-otp" element={<VerifyOtp />} />
              <Route path="/subscriptions" element={<Subscriptions />} />
              <Route path="/mission" element={<Mission />} />
              <Route path="/services" element={<Services />} />
              <Route path="/core-values" element={<CoreValues />} />
              <Route path="/tenders" element={<Tenders />} />
              <Route path="/reviews" element={<Reviews />} />
              <Route path="/privacy-policy" element={<PrivacyPolicy />} />
              <Route path="/upload-documents" element={<UploadDocuments />} />
              <Route path="/contact" element={<ContactUs />} />
              <Route path="/protected" element={<PrivateRoute auth={auth}><ProtectedComponent /></PrivateRoute>} />
            </Routes>
          </Suspense>
        </Layout>
      </ErrorBoundary>
    </Router>
  );
}

// Server startup
async function startServer() {
  await connectToMongo();
  app.listen(port, () => {
    console.log(`Server is running on port ${port}`);
  });
}

startServer();

process.on('SIGINT', async () => {
  try {
    await client.close();
    console.log('MongoDB connection closed');
    process.exit(0);
  } catch (error) {
    console.error('Error during graceful shutdown', error);
    process.exit(1);
  }
});

export default App;
