import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

const Animation = () => {
  const [stage, setStage] = useState(0);
  const containerRef = useRef(null);
  const [dimensions, setDimensions] = useState({ width: 1000, height: 600 });

  useEffect(() => {
    const timer = setTimeout(() => {
      if (stage < 5) {
        setStage(stage + 1);
      } else {
        setStage(0);
      }
    }, 2000);

    return () => clearTimeout(timer);
  }, [stage]);

  useEffect(() => {
    const updateDimensions = () => {
      if (containerRef.current) {
        const { width } = containerRef.current.getBoundingClientRect();
        setDimensions({ width, height: width * 0.6 }); // Maintain 10:6 aspect ratio
      }
    };

    updateDimensions();
    window.addEventListener('resize', updateDimensions);
    return () => window.removeEventListener('resize', updateDimensions);
  }, []);

  const skyColor = stage >= 2 ? "#87CEEB" : "#4A4A4A";
  const groundColor = stage >= 2 ? "#8B4513" : "#1A1A1A";

  return (
    <div ref={containerRef} style={{ width: '100%', maxWidth: '1000px', margin: '0 auto' }}>
      <svg
        viewBox={`0 0 1000 600`}
        width={dimensions.width}
        height={dimensions.height}
        style={{ display: 'block', maxWidth: '100%', height: 'auto' }}
      >
        {/* Sky and Ground */}
        <motion.rect width="1000" height="400" fill={skyColor} animate={{ fill: skyColor }} />
        <motion.rect y="400" width="1000" height="200" fill={groundColor} animate={{ fill: groundColor }} />

        {/* Sun/Moon */}
        <motion.circle
          cx="900"
          cy="100"
          r="50"
          fill={stage >= 2 ? "#FFD700" : "#FFFFFF"}
          animate={{
            fill: stage >= 2 ? "#FFD700" : "#FFFFFF",
            cx: stage >= 2 ? 900 : 100,
          }}
          transition={{ duration: 1 }}
        />

        {/* ... (rest of the SVG content remains the same) ... */}

        {/* Completed Project */}
        <AnimatePresence>
          {stage === 5 && (
            <motion.g
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ duration: 0.5 }}
            >
              <rect x="300" y="100" width="400" height="400" fill="#3F51B5" />
              <rect x="350" y="150" width="100" height="100" fill="#BBDEFB" />
              <rect x="550" y="150" width="100" height="100" fill="#BBDEFB" />
              <rect x="350" y="300" width="100" height="100" fill="#BBDEFB" />
              <rect x="550" y="300" width="100" height="100" fill="#BBDEFB" />
              <polygon points="300,100 500,20 700,100" fill="#303F9F" />

              {/* Vyvsai logo and tagline */}
              <circle cx="500" cy="550" r="40" fill="#FF0000" />
              <text x="500" y="560" textAnchor="middle" fill="#FFFFFF" fontSize="24" fontWeight="bold">Vyvsai</text>
              <text x="500" y="590" textAnchor="middle" fill="#000000" fontSize="16" fontWeight="bold">Building the Future with AI</text>
            </motion.g>
          )}
        </AnimatePresence>
      </svg>
    </div>
  );
};

export default Animation;
