import axios from 'axios';
import React, { useState, useEffect } from 'react';

axios.defaults.baseURL = 'http://localhost:5000';

const Tenders = () => {
  const [formData, setFormData] = useState({
    state: '',
    district: '',
    department: '',
    showExpired: false
  });
  const [tenders, setTenders] = useState([]);
  const [dropdownOptions, setDropdownOptions] = useState({
    states: [],
    districts: [],
    departments: []
  });

  useEffect(() => {
    fetchTendersData();
  }, [formData]);

  const fetchTendersData = async () => {
    try {
      const response = await axios.post('/api/tenders', formData);
      setTenders(response.data.tenders);
      setDropdownOptions(response.data.dropdownOptions);
    } catch (error) {
      console.error('Error fetching tenders data:', error.message);
      alert('Failed to fetch tenders data. Please try again later.');
    }
  };

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === 'checkbox' ? checked : value
    });
  };

  return (
    <div>
      <form>
        <label>
          State:
          <input
            type="text"
            name="state"
            value={formData.state}
            onChange={handleInputChange}
          />
        </label>
        <label>
          District:
          <input
            type="text"
            name="district"
            value={formData.district}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Department:
          <input
            type="text"
            name="department"
            value={formData.department}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Show Expired:
          <input
            type="checkbox"
            name="showExpired"
            checked={formData.showExpired}
            onChange={handleInputChange}
          />
        </label>
      </form>
      <div>
        <h2>Tenders</h2>
        <ul>
          {tenders.map((tender, index) => (
            <li key={index}>{tender.name}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Tenders;
