import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Papa from 'papaparse';

const CSVTenderDetails = () => {
  const { id } = useParams();
  const [tender, setTender] = useState(null);

  useEffect(() => {
    const fetchTenderDetails = async () => {
      try {
        const response = await fetch('/path/to/your/tenders.csv');
        const reader = response.body.getReader();
        const result = await reader.read();
        const decoder = new TextDecoder('utf-8');
        const csv = decoder.decode(result.value);
        const results = Papa.parse(csv, { header: true });
        
        const foundTender = results.data.find(tender => tender.id === id);
        setTender(foundTender);
      } catch (error) {
        console.error('Error fetching tender details:', error);
      }
    };

    fetchTenderDetails();
  }, [id]);

  if (!tender) {
    return <div>Loading...</div>;
  }

  return (
    <div className="container my-5 pt-4">
      <h2 className="text-center mb-4"><strong>Tender Details</strong></h2>
      <div className="card">
        <div className="card-body">
          <table className="table table-bordered table-striped">
            <thead className="thead-dark">
              <tr>
                <th scope="col">Field</th>
                <th scope="col">Details</th>
              </tr>
            </thead>
            <tbody>
              {Object.entries(tender).map(([key, value]) => (
                <tr key={key}>
                  <th scope="row">{key}</th>
                  <td>{value}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default CSVTenderDetails;
